    @PostConstruct
    public void initApp() {

        if (roleService.shouldInitialize()) {
            setDefaultRoles();


            addDefaultUsersWithAddresses();

            if (isTestRun)
                addDefaultTestProducts();
            else {
                addDefaultProducts();
                addDefaultReviews();
                addDefaultDeals();
                addDefaultCoupons();
                addDefaultOrders();
                addDefaultCartItems();
            }


        }
    }

    @Transactional
    public void setDefaultRoles() {

        roleService.saveRoleFor(UserRole.USER);
        roleService.saveRoleFor(UserRole.ADMIN);
        roleService.saveRoleFor(UserRole.INVENTORY_MANAGER);

    }

    private static final Logger log = LoggerFactory.getLogger(AppInitializationService.class);


    @Transactional
    public void addDefaultUsersWithAddresses() {


        String defaultPassword = "password";
        allUsers.add(createAppUserWithAddress("user", defaultPassword));
        allUsers.add(createAppUserWithAddress("JonSnow", defaultPassword));
        allUsers.add(createAppUserWithAddress("Ned", defaultPassword));
        allUsers.add(createAppUserWithAddress("Robert", defaultPassword));

        userService.addInventoryManager(createRegisterRequestWith("manager", defaultPassword));
        userService.addAdmin(createRegisterRequestWith("admin", defaultPassword));


    }

    private User createAppUserWithAddress(String appUser, String password) {
        User user = userService.addUser(createRegisterRequestWith(appUser, password));
        addShippingAddressFor(user);
        return user;
    }

    @Transactional
    public void addShippingAddressFor(User user) {

        shippingAddressService.addAddress(user, creatShippingAddressRequest(user.getFirstName()));


    }

    public static ShippingAddressRequest creatShippingAddressRequest(String name) {
        ShippingAddressRequest shippingAddressRequest = new ShippingAddressRequest();
        shippingAddressRequest.setCity("WinterFell");
        shippingAddressRequest.setLandmark("Castle");
        shippingAddressRequest.setName(name);
        shippingAddressRequest.setPhone("78121545");
        shippingAddressRequest.setStreet("The Castle Street");
        shippingAddressRequest.setState("The North");
        shippingAddressRequest.setZipcode("4823873");
        return shippingAddressRequest;
    }

