    private static int getRandomNumberBetweenTenToHundred() {

        int min = 10;
        int max = 100;

        Random r = new Random();
        return r.nextInt((max - min) + 1) + min;
    }

    DealRequest createFor(Product product) {
        DealRequest dealRequest = new DealRequest();
        dealRequest.setPrice(product.getPrice() - getRandomNumberBetweenTenToHundred());
        dealRequest.setProductId(product.getProductId());
        return dealRequest;
    }

    @Transactional
    public void addDefaultDeals() {

        allProducts.stream()
                .filter(product -> product.getPrice() > 101 && product.getPrice() % 5 ==0)
                .forEach(product ->  {
                    dealService.addDeal(createFor(product));
                    });


    }

    @Transactional
    public void addDefaultCoupons() {

        IntStream.rangeClosed(1, 20).forEach(i -> couponService.addCoupon(createCouponRequest(i)));


    }



    private CouponRequest createCouponRequest(int i) {
        CouponRequest couponRequest= new CouponRequest();
        couponRequest.setName("coupon" + i);
        Long amount = 50L + (i * getRandomNumberBetweenTenToHundred());
        couponRequest.setAmount(amount);
        return couponRequest;
    }



    CartItemRequest createForCart(Product product){
        CartItemRequest cartItemRequest = new CartItemRequest();
        cartItemRequest.setProductId(product.getProductId());
        cartItemRequest.setQuantity(1);
        return cartItemRequest;

    }
    @Transactional
    public void addDefaultOrders() {

        int countOfProducts = 3;
        Map<User, List<Product>> userProducts = getUserProductMapWithCount(countOfProducts);


        userProducts.forEach((user,products)->{



            products.forEach(product -> {
                cartService.addToCart(user,createForCart(product));
            });

            Cart cart = cartService.getCart(user);
            List<ShippingAddress> addresses =shippingAddressService.findAllByUser(user);

            if(addresses.size() > 0){
                ShippingAddress shippingAddress = addresses.get(0);
                Order order = orderService.createOrderWith(user,shippingAddress, cart);
                orderService.setOrderStatusToCompleted(order,user);
            }

        });

    }

    @Transactional
    public void addDefaultCartItems() {
        int countOfProducts = 3;
        Map<User, List<Product>> userProducts = getUserProductMapWithCount(countOfProducts);


        userProducts.forEach((user,products)->{



            products.forEach(product -> {
                cartService.addToCart(user,createForCart(product));
            });



        });
    }

    public Map<User, List<Product>> getUserProductMapWithCount(int countOfProducts) {
        int startIndex=0;
        int endIndex=startIndex + countOfProducts;

        Map<User,List<Product>> userProducts = new HashMap<>();

        for(int i=0;i<allUsers.size();i++){
          User  user=allUsers.get(i);
            List<Product> productsForUser = new ArrayList<>();

            for(int j=startIndex;j<endIndex;j++) {
                productsForUser.add(allProducts.get(j));

            }

            startIndex =endIndex ;
            endIndex = startIndex + 5;
            userProducts.put(user,productsForUser);


        }
        return userProducts;
    }

    @Transactional
    public void addDefaultReviews() {



        allUsers.forEach(user -> {

            allProducts.forEach(product -> {
                reviewService.addReview(user,createDefaultReviewRequest(user,product));

            });
        });


    }

    public static ReviewRequest createDefaultReviewRequest(User user, Product product) {
        List<String> reviews = Arrays.asList("Very Bad", "Bad", "Ok", "Good", "Wonderful");

        int index = (product.getName().length() + user.getUserName().length()) % 5;
        String review = reviews.get(index);
        int rating = index + 1;

        ReviewRequest reviewRequest = new ReviewRequest();
        reviewRequest.setProductId(product.getProductId());
        reviewRequest.setComment(review);
        reviewRequest.setRating(rating);
        return reviewRequest;
    }

    private static Double getRandomProductRating() {
        Integer min = 1;
        Integer max = 5;
        Random r = new Random();
        Integer result = r.nextInt((max - min) + 1) + min;
        return result.doubleValue();
    }

    public Product createWithDefault(String name, String category, double price, String description, String manufacturer, String imageUrl) {
        Product product = new Product(name, category, price, description, manufacturer, imageUrl);
        product.setAvailableItems(25);
        product.setOverAllRating(getRandomProductRating());
        return product;
    }

